{
  "class_purpose": "AbstractResponse is the common base class for all Kafka protocol response types. It encapsulates shared functionality such as serialization/deserialization, error counting, throttle‑time handling and provides the API‑key identification for a response. Concrete response classes extend this class and implement the abstract methods to expose response‑specific data.",
  "key_fields": [
    {
      "name": "apiKey",
      "description": "The ApiKeys enum value that identifies which Kafka API this response belongs to. It is set in the constructor and never changes."
    },
    {
      "name": "DEFAULT_THROTTLE_TIME",
      "description": "Constant (value 0) representing the default throttle time when a response schema does not contain a throttle‑time field."
    }
  ],
  "key_methods": [
    {
      "name": "AbstractResponse(ApiKeys apiKey)",
      "description": "Protected constructor that stores the API key for the concrete response instance."
    },
    {
      "name": "toSend(ResponseHeader header, short version)",
      "description": "Creates a {@link Send} object that contains the full wire‑format response (size prefix, header and body) ready to be written to a socket."
    },
    {
      "name": "serializeWithHeader(ResponseHeader header, short version)",
      "description": "Serializes the response together with its header into a {@link ByteBuffer} but **without** the length prefix. Used internally by {@code toSend}."
    },
    {
      "name": "serialize(short version)",
      "description": "Serializes only the response body (no header, no length prefix) into a {@link ByteBuffer}. Primarily exposed for testing."
    },
    {
      "name": "errorCounts()",
      "description": "Abstract method that concrete responses must implement to return a map of {@link Errors} → occurrence count for all errors present in the response (including top‑level and per‑entity errors)."
    },
    {
      "name": "errorCounts(Errors error)",
      "description": "Utility that creates a singleton error‑count map for a single error value."
    },
    {
      "name": "errorCounts(Stream<Errors> errors)",
      "description": "Utility that groups a stream of Errors and returns a map with the frequency of each error."
    },
    {
      "name": "errorCounts(Collection<Errors> errors)",
      "description": "Utility that iterates over a collection of Errors and builds the error‑count map using {@code updateErrorCounts}."
    },
    {
      "name": "apiErrorCounts(Map<?, ApiError> errors)",
      "description": "Converts a map whose values are {@link ApiError} objects into an error‑count map of the underlying {@link Errors}."
    },
    {
      "name": "updateErrorCounts(Map<Errors, Integer> errorCounts, Errors error)",
      "description": "Increments the count for the supplied error in the provided map (adds a new entry if the error was not present)."
    },
    {
      "name": "parseResponse(ByteBuffer buffer, RequestHeader requestHeader)",
      "description": "Static factory that parses a response from a buffer that contains both the {@link ResponseHeader} and the response payload. It validates correlation IDs and then delegates to the overload that uses the ApiKey."
    },
    {
      "name": "parseResponse(ApiKeys apiKey, ByteBuffer buffer, short version)",
      "description": "Static dispatch method that selects the concrete response class based on the supplied ApiKey and invokes its specific {@code parse} method. Throws an AssertionError for unknown/unsupported ApiKeys."
    },
    {
      "name": "shouldClientThrottle(short version)",
      "description": "Indicates whether a client should apply its own throttling for a response of the given version when the response carries a non‑zero throttle time. Default implementation returns {@code false}; subclasses can override when needed."
    },
    {
      "name": "apiKey()",
      "description": "Returns the ApiKeys value stored in the instance."
    },
    {
      "name": "throttleTimeMs()",
      "description": "Abstract method that concrete responses implement to expose the throttle time (in ms) contained in the response schema, or 0 if the schema does not have such a field."
    },
    {
      "name": "maybeSetThrottleTimeMs(int throttleTimeMs)",
      "description": "Abstract method that concrete responses implement to set the throttle time field when the schema supports it; otherwise it is a no‑op."
    },
    {
      "name": "toString()",
      "description": "Overrides {@code Object#toString} to return the string representation of the underlying response data object."
    }
  ]
}

