{
  "class_purpose": "Represents the response sent by a Kafka broker for an UPDATE_METADATA request. It wraps the generated protocol data structure (UpdateMetadataResponseData) and provides convenient access to the error information, parsing logic, and the abstract‑response contract required by the Kafka client/server protocol layer.",
  "key_fields": [
    {
      "name": "data",
      "description": "The protocol‑specific payload (UpdateMetadataResponseData) that holds all fields defined by the UPDATE_METADATA response schema, including the error code. It is immutable and is the source for all accessor methods in this class."
    }
  ],
  "key_methods": [
    {
      "name": "UpdateMetadataResponse(UpdateMetadataResponseData data)",
      "description": "Constructor that initializes the response with the given protocol data object and registers the API key (UPDATE_METADATA) with the superclass."
    },
    {
      "name": "error()",
      "description": "Returns the top‑level error for the response by converting the error code stored in `data` to the corresponding `Errors` enum."
    },
    {
      "name": "errorCounts()",
      "description": "Overrides `AbstractResponse.errorCounts()` to provide a map containing the count of each error present in the response (currently only the top‑level error)."
    },
    {
      "name": "throttleTimeMs()",
      "description": "Overrides the default throttle‑time accessor; for this response type the throttle time is not part of the schema, so it always returns the constant `DEFAULT_THROTTLE_TIME`."
    },
    {
      "name": "maybeSetThrottleTimeMs(int throttleTimeMs)",
      "description": "No‑op implementation because the UPDATE_METADATA response schema does not include a throttle‑time field."
    },
    {
      "name": "parse(ByteBuffer buffer, short version)",
      "description": "Static factory method that deserialises a `ByteBuffer` containing a binary UPDATE_METADATA response into an `UpdateMetadataResponse` instance using the generated `UpdateMetadataResponseData` parser."
    },
    {
      "name": "data()",
      "description": "Overrides `AbstractResponse.data()` to expose the underlying `UpdateMetadataResponseData` object."
    }
  ]
}
